import React, {useContext, useState, useEffect} from 'react'
import { StateContext } from '../../context/GlobalState'
import ProductItem from './ProductItem'
import './product.css'

const url = "https://e-nk.github.io/projdb/db.json"


function ProductGrid() {


    const {products} = useContext(StateContext)
    const [search, setSearch] = useState("");



    useEffect(() => {
        fetch(url)
        .then(response => response.json()) // get json data from api
        .then(data => setProducts(data)) // save data to `state` 
    }, [])



      products = products.filter(
        (userObj) => userObj.title.toLowerCase().indexOf(search.toLowerCase()) > -1
      );

    return(
        <div>
            <div className="col-sm-4">                
                    <div className="form-group">
                        <input type="text" name="" placeholder="Search product" className="form-control" id=""/>
                    </div>
            </div>

        <section className="products">

            {
                products.map((product) => <ProductItem key={product.id} product={product}/>)
            }
        </section>
        </div>
    ) 
}

export default ProductGrid
